import React from 'react'
import './Freetrial.scss'
import {useState} from 'react'
import Footer from "components/footers/MiniCenteredFooter2.js";
import Header from "components/headers/light2.js";
import styled from "styled-components";
//import Header from "./header2";
import gratuit from '../../images/sanoGratuit.png'
import { Chart } from 'primereact/chart'

//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome/'

function Freetrial() {

  const [n, setNumber] = useState(0)
  const [quizStart, setQuizStarted] = useState(false);
  
  const [goal, setGoal] = useState()
  const [goal2, setGoal2] = useState()
  const [veg, setVeg] = useState()
  const [whey, setWhey] = useState()
  const [work, setWork] = useState()
  const [actv, setActv] = useState()
  const [gender, setGender] = useState()
  const [taille, setTaille] = useState()
  const [age, setAge] = useState()
  const [poid, setPoid] = useState()

  const [mb, setMb] =useState()
  const [bej, setBej] =useState()
  const [,] =useState()
  const [,] =useState()
  const [,] =useState()
  const [,] =useState()
  const [,] =useState()
  const [,] =useState()
  const [,] =useState()


  const [,] =useState()

  const nap = [0, 0, 1.375, 1.4675, 1.56, 1.6, 1.64, 1.73, 1.82]

  function hundleStart() {
    setQuizStarted(true)
    setNumber(n+1)
  }


  function hundleQuizz(x){
  if (x == 1) {
    setGoal("lose")
  }
  if (x == 2) {
    setGoal("gain")
  }
  if (x == 3) {
    setGoal('maintain')
  }
  if (x == 4) {
    setGoal2('testo')
  }
  if (x == 5) {
    setGoal2('brain')
  }
  if (x == 6) {
    setGoal2('energie')
  }
  if (x == 7) {
    setVeg(true)
  }
  if (x == 8) {
    setVeg(false)
  }
