import React from "react";
import tw from "twin.macro";
import { css } from "styled-components/macro"; //eslint-disable-line
import styled from 'styled-components';
import AnimationRevealPage from "helpers/AnimationRevealPage.js";
import Hero from "components/hero/TwoColumnWithVideo.js";
//import Features from "components/features/ThreeColSimple.js";
import MainFeature from "components/features/TwoColWithButton.js";
// import MainFeature2 from "components/features/TwoColSingleFeatureWithStats2.js";
// import TabGrid from "components/cards/TabCardGrid.js";
// import Testimonial from "components/testimonials/ThreeColumnWithProfileImage.js";
// import DownloadApp from "components/cta/DownloadApp.js";
import Footer from "components/footers/MiniCenteredFooter.js";
import FeatureWithSteps from "components/features/TwoColWithSteps.js";

// import chefIconImageSrc from "images/chef-icon.svg";
// import celebrationIconImageSrc from "images/celebration-icon.svg";
// import shopIconImageSrc from "images/shop-icon.svg";

export default () => {
  const Subheading = tw.span`tracking-wider text-sm font-medium`;
  const HighlightedText = tw.span`bg-primary-500 text-gray-100 px-4 transform -skew-x-12 inline-block`;
  const Incline = styled.div`
  --tw-translate-x: 0;
    --tw-translate-y: 0;
    --tw-rotate: 0;
    --tw-skew-x: -12deg;
    --tw-skew-y: 0;
    --tw-scale-x: 1;
    --tw-scale-y: 1;
    transform: translateX(var(--tw-translate-x)) translateY(var(--tw-translate-y)) rotate(var(--tw-rotate)) skewX(var(--tw-skew-x)) skewY(var(--tw-skew-y)) scaleX(var(--tw-scale-x)) scaleY(var(--tw-scale-y));
  `;
  const HighlightedTextInverse = tw.span`bg-gray-100 text-primary-500 px-4 transform -skew-x-12 inline-block`;
  const Description = tw.span`inline-block mt-8`;
  const imageCss = tw`rounded-4xl`;



  return (
    <AnimationRevealPage>
      <Hero
        heading={<>PLAN NUTRITIONNEL<Incline><HighlightedText> PERSONNALISÉ</HighlightedText></Incline></>}
        description="Korsano vous aide a mieux manger, en prennat compte vos objectifs et vos preferences alimentaires"
        imageSrc="https://cdn.dribbble.com/userupload/2850124/file/original-999c98affe282a81c153721d3d5b872b.jpeg?compress=1&resize=1200x900"
        imageCss={imageCss}
        imageDecoratorBlob={true}
        primaryButtonText="Essai gratuit"
        watchVideoButtonText="Nos Services"
      />
      <MainFeature
        subheading={<Subheading>Mens Sana in Corpore Sano</Subheading>}
        heading={
          <>
            TU MANGES MAL
            <wbr /> <Incline><HighlightedText>BALTRINGUE</HighlightedText></Incline>
          </>
        }
        description={
          <Description>
            Prisse ou perte de poids ? Booster ta testo ou tes capacité cognitive ? Fait le choix d'arreter d'etre une merde et achete batard 
            <br />
            <br />
            Remplis notre essai gratuit pour voir si mange mal ou si tu as des carrence
          </Description>
        }
        buttonRounded={false}
        textOnLeft={false}
        primaryButtonText="Essai gratuit"
        imageSrc={
          "https://images.unsplash.com/photo-1460306855393-0410f61241c7?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=768&q=80"
        }
        imageCss={imageCss}
        imageDecoratorBlob={true}
        imageDecoratorBlobCss={tw`left-1/2 -translate-x-1/2 md:w-32 md:h-32 opacity-25`}
      />



   <FeatureWithSteps
        subheading={<Subheading>ÉTAPES</Subheading>}
        heading={
          <>
            Facile à  <Incline><HighlightedText>utiliser.</HighlightedText></Incline>
          </>
        }
        textOnLeft={false}
        imageSrc='https://cdn.dribbble.com/users/56226/screenshots/14237975/media/fcde347c89614d3bd2a7383041a3ef80.jpg?compress=1&resize=1000x750&vertical=top'
        imageDecoratorBlob={true}
        decoratorBlobCss={tw`xl:w-40 xl:h-40 opacity-15 -translate-x-1/2 left-1/2`}
      />


      <Footer />

    </AnimationRevealPage>
  );
}
